componentPatterns.css:
.modal-overlay {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: grid;
  place-items: center;
  padding: 16px; /* Ensure modal doesn't touch edges on small screens */
}

.modal-content {
  background-color: var(--rootsdk-background-primary);
  border: 1px solid var(--rootsdk-border);
  border-radius: 8px;
  box-shadow: 0 0 12px rgba(0, 0, 0, 0.5);
  max-width: 688px;
  width: 100%; /* Responsive: fill available width on small screens */
  max-height: 90vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 12px 20px;
  background-color: var(--rootsdk-background-primary);
}

.modal-title {
  font-size: 20px;
  font-weight: 450;
  line-height: 32px;
}

.modal-body {
  flex: 1;
  padding: 0 24px;
  overflow-y: auto;
}

