componentPatterns.css:
.icon-button {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 4px;
  border-radius: 6px;
  aspect-ratio: 1;
  cursor: pointer;
  color: var(--rootsdk-text-tertiary);
  transition: background-color 150ms, color 150ms;
}

.icon-button:hover {
  background-color: var(--rootsdk-highlight-strong);
  color: var(--rootsdk-text-primary);
}

.list-item {
  padding: 8px 12px;
  border-radius: 8px;
  cursor: pointer;
  transition: background-color 150ms;
}

.list-item:hover {
  background-color: var(--rootsdk-highlight-normal);
}

/* Focus ring pattern - apply to focusable elements */
.focusable:focus {
  outline: none;
  box-shadow: 0 0 0 2px var(--rootsdk-brand-primary);
}

/* For elements that need focus within (like custom selects) */
.focusable:focus-within {
  outline: none;
  box-shadow: 0 0 0 2px var(--rootsdk-brand-primary);
}

/* Focus-visible for keyboard-only focus */
.focusable:focus-visible {
  outline: none;
  box-shadow: 0 0 0 2px var(--rootsdk-brand-primary);
}

